package com.shiro.springboot_jsp_shiro.shiro.cache;import org.apache.shiro.cache.Cache;import org.apache.shiro.cache.CacheException;import org.apache.shiro.cache.CacheManager;//自定义缓存管理器public class RedisCacheManager implements CacheManager {    //s是认证或者授权缓存管理器的名称    @Override    public <K, V> Cache<K, V> getCache(String cacheName) throws CacheException {        return new RedisCache<K,V>(cacheName);    }}