package com.shiro.springboot_jsp_shiro.utils;import org.springframework.beans.BeansException;import org.springframework.context.ApplicationContext;import org.springframework.context.ApplicationContextAware;import org.springframework.stereotype.Component;//获取SpringBoot创建好的类的工厂工具类@Componentpublic class ApplicationContextUtils implements ApplicationContextAware {    private static ApplicationContext context;    @Override    public void setApplicationContext(ApplicationContext applicationContext) throws BeansException {        this.context = applicationContext;    }    //通过传递bean的名字从Spring工厂中获取相应的bean    public static Object getBeanByName(String beanName){        return context.getBean(beanName);    }}